document.addEventListener("DOMContentLoaded",()=>{let e=Array.prototype.slice.call(document.querySelectorAll(".navbar-burger"),0);e.forEach(e=>{e.addEventListener("click",()=>{let t=e.dataset.target,n=document.getElementById(t);e.classList.toggle("is-active"),n.classList.toggle("is-active")})})}),document.getElementById("keypair").addEventListener("click",()=>{fetch("/@admin/tools",{method:"POST",body:JSON.stringify({function:"keypair"}),headers:{"Content-type":"application/json"}}).then(e=>e.json().then(e=>{document.getElementById("publickey").value=e.publickey,document.getElementById("privatekey").value=e.privatekey}))}),document.getElementById("encrypt").addEventListener("click",()=>{let e;fetch("/@admin/tools",{method:"POST",body:JSON.stringify({function:"encrypt",cleartext:document.getElementById("cleartext").value,key:document.getElementById("pubkey").value}),headers:{"Content-type":"application/json"}}).then(e=>e.json().then(e=>{document.getElementById("encryptedvalue").value=e.encrypted}))});